mapper.select:
select max(CheckNum)
from register_info
where ExamItemName = #{modality}
  and CheckNum like #{param}

select max(PatientID)
from register_info
where ExamItemName = #{modality}
  and PatientID like #{param}

select max(RecordID)
from register_info
where ExamItemName = #{modality}
  and RecordID like #{param}
